css:
.order-info__toggle {
    display: none !important;
}


.product__short-stock {
display: none !important;
}

.products-carousel__title a, span {
 font-family: 'Marcellus', serif; 
}

body, p, em, b, small, strong, h3, {
  font-family: 'Prompt', sans-serif;
}

h1, h2, h4, h5, h6, {
  font-family: 'Marcellus', serif;
}

@media(max-width: 767px){
  .banners-grid .col-12.col-lg-3 {
    max-width: 50%;
    flex: 0 0 50%;
    padding-right: 2px;	 
    padding-left: 2px;	
} 
  .banners-grid .row .col-12.col-lg-3.mb-3.mb-lg-0 {margin-bottom: .2rem}

 .i-play{
    font-size: 25px;
    background-color: red;
    border-radius: 15px;
    color: white;
}
}

.search__box {
  font-family: 'Prompt'
}

.top-bar__countdown {
    font-size: 14px;
    font-weight: 800;
    background: #fc2792;
    color: #ffffff;
}

.header {
    background: white;
}

.form-control {
    background-color: #ffffff;
}

main .header__search {
  font-family: 'Prompt';
}

main .product-card {
  font-family: 'Prompt';
}

main .breadcrumb-item a {
    color: #888888;
}

main .breadcrumb-item a:hover {
    color: #000000;
}

main .breadcrumb-item+.breadcrumb-item:before {
    color: #888888;
}

.form-control-lg {
  font-size: 1rem;
}

.header__search-btn {
  font-size: 1rem;
  padding-bottom: 13px;
}

.i-search{
  font-family: Prompt;
  color: #fc2792;
}

.header__buttons .btn {
    color: #FC2792;
}

.header__toggler-icon {
    color: #FC2792;
}

.header__category:hover {
    background: #FC2792;
    text-decoration: none;
    color: white;
}

.minicart__btn-cart {
    border-color: black;
    color: black;
}

.minicart__btn-cart:hover{
    border-color: #FC2792;
    color: #FC2792;
}

.cart-item .close {
    color: red;
}


div>.header__nav .header__category {
  color: #FC2792;
  font-size: 12px;
}

div>.header__nav--full a {
    padding: 0 14px;
}

/*COR NOME DO PRODUTO*/
.product__name {
    color: #000000 !important;
}

.product__buy .quantity-selector__btn-container button {
    border: none;
}

.product .share-network-whatsapp {
    color: #888888;
}

.products-carousel__title a, .products-carousel__title span {
    color: #000000;
}

.product__free-shipping-from {
    color: #888888 !important;
}


.product-description .container .lead{
  color: #888888 !important;
}

.prices__value{
  color: black !important;
}

.badge-secondary {
    background-color: black;
}

/*BOTÕES*/

.btn-outline-secondary{
    border-color: black;
    color: black;
}

.btn-outline-secondary:hover{
    border-color: #FC2792;
    color: #FC2792;
    background-color: none !important;
}

.btn-sm .product__favorite{
  color: #888888;
}

.btn-light, .btn-light{
    color: #5a5a5a !important;
    border-color: #5a5a5a;
}

.btn-secondary{
    color: #000000 !important;
    border-color: #000000 !important;
    background-color: #f5f5f5 !important;
}

/*BOTÃO LIXEIRA*/
.btn-danger {
    background-color: #ff0000;
    color: #ffffff;
}

/*COR TEXTOS CHECKOUTS*/
.checkout__step--current{
  color: gray !important;
}

.discount-applier__coupon-btn {
    color: #000000 !important;
    border-color: #000000 !important;
    background-color: #f5f5f5 !important;
}

.list-group-item+.list-group-item {
    border-top-width: 0;
    height: 80px;
}

shipping-calculator__option>small {
    position: absolute;
    right: -5px;
    top: -5px;
    line-height: 1.3;
}


.login__info-title {
    color: black !important;
}

.app__contact span:not(:first-child) {
    color: gray;
}

.login__email-btn {
  color: white;
  border-color: none;
}

.login__email-btn, .btn-primary{
  color: white;
  border-color: none;
}

#storefront-app>div>h1 {
    color: #888888 !important;
}

main .search-engine__count {
    color: #a1a1a1;
}

main .product .shipping-calculator__option strong {
    color: black;
}

.discount-applier__intro {
    color: #888888 !important; 
}

.product-card__buy.fade {
    color: white;
}

#go-to-top{
    border-color: black;
    background: none;
    color: black;
}

main .footer p {
    display: none;
}

.footer__store {
    color: gray;
}
